import React, { useState } from "react";
import axios from "axios";
import { motion } from "framer-motion";

function App() {
    const [file, setFile] = useState(null);
    const [prediction, setPrediction] = useState(null);
    const [loading, setLoading] = useState(false);

    const handleFileChange = (e) => setFile(e.target.files[0]);

    const handleUpload = async () => {
        if (!file) return alert("Please select a file first!");

        const formData = new FormData();
        formData.append("file", file);

        try {
            setLoading(true);
            setPrediction(null);

            const response = await axios.post("http://localhost:8000/predict", formData);
            console.log("Response:", response.data);

            const { classification_report, predictions } = response.data;

            setPrediction({
                report: classification_report,
                results: predictions,
            });
        } catch (error) {
            console.error("Error uploading file:", error);
            alert("Failed to get prediction.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen bg-gradient-to-r from-blue-500 to-purple-600 flex flex-col items-center justify-center p-4">
            <motion.h1
                initial={{ y: -20, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                transition={{ duration: 0.5 }}
                className="text-4xl font-extrabold mb-6 text-white drop-shadow-lg"
            >
                ðŸŒŸ FedShield ðŸŒŸ
            </motion.h1>

            <motion.div
                initial={{ scale: 0.9, opacity: 0 }}
                animate={{ scale: 1, opacity: 1 }}
                transition={{ duration: 0.5 }}
                className="bg-white p-6 rounded-2xl shadow-lg w-80"
            >
                <input
                    type="file"
                    onChange={handleFileChange}
                    className="w-full mb-4 p-2 border rounded"
                />
                <button
                    onClick={handleUpload}
                    className="w-full bg-gradient-to-r from-blue-500 to-purple-600 hover:opacity-90 text-white font-semibold py-2 rounded-2xl transition"
                >
                    ðŸš€ Upload & Predict
                </button>

                {loading && (
                    <motion.div
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        transition={{ duration: 0.3 }}
                        className="text-blue-500 font-semibold mt-4 text-center"
                    >
                        ðŸ”„ Processing...
                    </motion.div>
                )}

                {prediction && (
                    <motion.div
                        initial={{ scale: 0.8, opacity: 0 }}
                        animate={{ scale: 1, opacity: 1 }}
                        transition={{ duration: 0.5 }}
                        className="mt-4 p-4 bg-green-200 text-green-800 rounded-xl shadow text-center"
                    >
                        <h2 className="text-xl font-bold mb-2">âœ… Prediction Results</h2>
                        {prediction.results.map((res, index) => (
                            <p key={index}>
                                <strong>Transaction {index + 1}:</strong> {res}
                            </p>
                        ))}
                        <h2 className="text-xl font-bold mt-4 mb-2">ðŸ“Š Classification Report</h2>
                        <pre className="text-sm">{JSON.stringify(prediction.report, null, 2)}</pre>
                    </motion.div>
                )}
            </motion.div>
        </div>
    );
}

export default App;
